import { StyleSheet, Text, View, Image, ScrollView } from 'react-native';

const CourseTile = (props)=>{
    return (
        <View style={styles.tile}>
        <Image
          style={styles.tileImage}
          source={require('../assets/courseImages/macbook-code.jpg')}
        />
        <View style={styles.tileDetail}>
          <Text style={styles.tileHeader}>{props.title}</Text>
          <Text style={styles.tileText}>{props.subtitlepwd}</Text>
        </View>
      </View>
    );
}

const styles = StyleSheet.create({
        header1: {
          fontSize: 32,
          fontWeight: '800',
          margin: 12,
        },
        container: {
          backgroundColor: '#fff',
          paddingTop: 56,
        },
        tilesContainer: {
          flex: 1,
          flexDirection: 'row',
          flexWrap: 'wrap',
          flexGrow: 0,
          flexShrink: 0,
          justifyContent: 'space-evenly',
      
        },
        tile: {
          flexBasis: '46%',//200
          borderRadius: 5,
          borderColor: '#B4B8DA',
          borderWidth: 1,
          borderStyle: 'solid',
          marginVertical: '2%',
      
        },
        tileImage: {
          width: '100%',
          maxHeight: 110,
          overflow: 'hidden',
        },
        tileDetail: {
          padding: 10,
        },
        tileHeader: {
          fontSize: 22,
          fontWeight: '600',
        },
        tileText: {
          fontSize: 16,
          fontWeight: '500',
        },
      
});

export default CourseTile;
